import React, { useState } from 'react';

const ObjectDetection = () => {
  const [originalImage, setOriginalImage] = useState(null);
  const [annotatedImage, setAnnotatedImage] = useState(null);
  const [counts, setCounts] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setIsLoading(true);
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:8000/detect', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      setOriginalImage(`data:image/jpeg;base64,${data.original_image}`);
      setAnnotatedImage(`data:image/jpeg;base64,${data.annotated_image}`);
      setCounts(data.counts);
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="p-4 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Object Detection</h1>
      
      <div className="mb-4">
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          className="mb-4"
        />
      </div>

      {isLoading && <div>Processing...</div>}

      {counts && (
        <div className="mb-4 p-4 border rounded">
          <h2 className="text-xl font-bold mb-2">Detected Objects:</h2>
          {Object.entries(counts).map(([className, count]) => (
            <div key={className}>
              {className}: {count}
            </div>
          ))}
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {originalImage && (
          <div>
            <h3 className="font-bold mb-2">Original Image:</h3>
            <img 
              src={originalImage} 
              alt="Original" 
              className="max-w-full border rounded"
            />
          </div>
        )}
        
        {annotatedImage && (
          <div>
            <h3 className="font-bold mb-2">Detected Objects:</h3>
            <img 
              src={annotatedImage} 
              alt="Annotated" 
              className="max-w-full border rounded"
            />
          </div>
        )}
      </div>
    </div>
  );
};

export default ObjectDetection;
